// import logo from './logo.svg';
import './App.css';
import { useState } from 'react'
import Header from './components/Header'
import Button from './components/Button'
import Word from './components/Word'
import LastThree from './components/LastThree'

function App() {
  var randomWords = require('random-words');
  var wd = require("word-definition");
  var rhyme = require('rhymes');

  const [currentWord, setWord] = useState("");
  const [rhymes, setRhymes] = useState([])

  const generateWord = async () => {
    var new_word = await randomWords();
    new_word = new_word.toUpperCase();
    setWord(new_word);
    getRhymes(new_word);
  }

  const randomInt = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
  }

  const getRhymes = (word) => {
    // wd.getDef(word, "en", null, (def) => {
    //   console.log(def)
    // })
    var rhyme_list = rhyme(word);
    var new_rhymes = [];

    for(var i = 0; i < 3; i++) {
      var randInt = randomInt(0, rhyme_list.length + 1)
      var rh = rhyme_list[randInt]['word']
      if (new_rhymes.includes(rh)) {
        i -= 1;
      } else {
        new_rhymes.unshift(rh)
      }
    }

    console.log(rhymes);
    setRhymes(rhymes);
  }

  return (
    <div className="App">
      <Header className = "head"/>
      <Button class = "btn" text = "Let's Go!" onClick = {generateWord}/>

      <Word word = {currentWord} class = "wrd"/>
      <LastThree lastThree = {rhymes} class = "subwrds"/>

    </div>
  );
}

export default App;
