// import logo from './logo.svg';
import './App.css';
import { useState } from 'react'
import Header from './components/Header'
import Button from './components/Button'
import Word from './components/Word'
import LastThree from './components/LastThree'

function App() {
  var randomWords = require('random-words');
  var wd = require("word-definition");

  const [currentWord, setWord] = useState("");
    const [lastThree, setLastThree] = useState(["", "", ""])

  const generateWord = () => {
    var new_word = randomWords();
    new_word = new_word.toUpperCase();
    setWord(new_word);
    updateLastThree(new_word);
  }

  const updateLastThree = (word) => {
    fetch("https://.wiktionary.org/w/api.php?action=query&prop=revisions&rvprop=content&format=json&titles=ride", {
      mode: 'no-cors',
      headers: {
        'Content-Type': "application/json"
      }
    })
    wd.getDef(word, "en", null, (def) => {
      console.log(def)
    })
    var new_three = lastThree;
    new_three.unshift(word);
    if (lastThree.length < 3) {
      setLastThree(new_three);
    } else {
      new_three.pop();
      setLastThree(new_three);
    }
    console.log(lastThree);
  }

  return (
    <div className="App">
      <Header />
      <Button class = "btn" text = "Generate Word!" onClick = {generateWord}/>

      <Word word = {currentWord} />
      <LastThree lastThree = {lastThree} />

    </div>
  );
}

export default App;
