// import logo from './logo.svg';
import './App.css';
import { useState } from 'react'
import Header from './components/Header'
import Button from './components/Button'
import Word from './components/Word'
import LastThree from './components/LastThree'

function App() {
  var randomWords = require('random-words');
  var wd = require("word-definition");
  var rhyme = require('rhymes');

  const [currentWord, setWord] = useState("");
    const [lastThree, setLastThree] = useState(["", "", ""])

  const generateWord = () => {
    updateLastThree(currentWord);
    var new_word = randomWords();
    new_word = new_word.toUpperCase();
    setWord(new_word);
  }

  const updateLastThree = (word) => {
    // wd.getDef(word, "en", null, (def) => {
    //   console.log(def)
    // })

    console.log(rhyme(word))

    var new_three = lastThree;
    new_three.unshift(word);
    if (lastThree.length < 3) {
      setLastThree(new_three);
    } else {
      new_three.pop();
      setLastThree(new_three);
    }
  }

  return (
    <div className="App">
      <Header className = "head"/>
      <Button class = "btn" text = "Let's Go!" onClick = {generateWord}/>

      <Word word = {currentWord} class = "wrd"/>
      <LastThree lastThree = {lastThree} class = "wrdhist"/>

    </div>
  );
}

export default App;
